import React, { useRef } from "react";
import ProductItem from "./ProductItem";
import withContext from "../withContext";
import { convertUTCDateToLocalDate } from "../utils/util.js";

const ProductList = props => {
  const { products } = props.context;

  const [selectedDate, setSearchDate] = React.useState(convertUTCDateToLocalDate(new Date().toISOString()));
  const dateInputRef = useRef(null);

  let handleChange = (e) => {
    setSearchDate(e.target.value)
  }

  return (
    <>
      <div className="hero is-primary">
        <div className="hero-body container">
          <h4 className="title">Our Products</h4>
        </div>
      </div>
      <br />
      <div className="container">
        <div className="column">
            <span className="title">Input a date to check shipping date on products</span><br />
            <input 
                type="date" data-display-mode="inline" 
                onChange={handleChange}
                value={selectedDate.split("T")[0]}
                ref={dateInputRef}
                min={new Date().toISOString().split("T")[0]}>
            </input>
        </div>
        <div className="column columns is-multiline">
          {products && products.length ? (
            products.map((product, index) => (
              <ProductItem
                product={product}
                key={index}
                addToCart={props.context.addToCart}
                orderDate={selectedDate}
              />
            ))
          ) : (
            <div className="column">
              <span className="title has-text-grey-light">
                No products found!
              </span>
            </div>
          )}
        </div>
      </div>
    </>
  );
};

export default withContext(ProductList);
