var moment = require('moment'); // require

const DATE_FORMAT = 'D-MMM-YYYY';
const SUNDAY = 0; // moment day index
const SATURDAY = 6; // moment day index
const WEEKENDS = [SATURDAY, SUNDAY];

export function findDaysToShip(product) {
    console.log(product)
    let weekendsIncluded = product.shipOnWeekends
    let maxDaysToShip = product.maxBusinessDaysToShip

    const current = new Date();
    console.log(current);
    let approxShipDate = new Date(current.setDate(current.getDate() + maxDaysToShip));
    console.log(approxShipDate);


    const stringDate = `${current.getDate()}-${current.getMonth()+1}-${current.getFullYear()}`;

    while (maxDaysToShip > 0) {
        // today.setDate(today.getDate() + maxDaysToShip);

        stringDate.add(1, 'day');

        // Skip weekends
        if (WEEKENDS.includes(stringDate.day())) {
            continue;
        }

        // Increment count
        maxDaysToShip--;
    }

    return stringDate;
}
