var moment = require('moment'); // require

export function findDaysToShip(product) {
    let maxDaysToShip = product.maxBusinessDaysToShip

    const current = new Date();
    let days = 0;

    if (!product.shipOnWeekends) {
        days = getFinalShipDate(current, maxDaysToShip);
        console.log(`day: ${days}`);
        return days;
    }

    let approxShipDate = new Date();
    approxShipDate = new Date(approxShipDate.setDate(current.getDate() + maxDaysToShip));
    console.log(approxShipDate);

    // const stringDate = `${current.getDate()}-${current.getMonth()+1}-${current.getFullYear()}`;


    return approxShipDate;
}

function getFinalShipDate(d1, maxDaysToShip) {
    let d2 = new Date(d1);
    d2 = new Date(d2.setDate(d2.getUTCDate()));
    let weekendCount = 0;
    let dayCount = 0;

    while ( maxDaysToShip > 0) {
        var day = d1.getDay();
        if (day === 6 || day === 0) {
            ++weekendCount;
        } else {
            dayCount++;
            maxDaysToShip--;
        }
        d1.setDate(d1.getDate() + 1);
    }

    d2 = new Date(d2.setDate(d2.getDate() + weekendCount + dayCount));
    return d2;
}
