import React, {Component} from 'react';
import './App.css';
import { findDaysToShip } from './utils/util';

import {
  MDBCard,
  MDBCardBody,
  MDBCardTitle,
  MDBCardText,
  MDBRow,
  MDBCol,
  MDBBtn
} from 'mdb-react-ui-kit';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      products: []
    }
  }

  componentDidMount() {
    let url = "http://localhost:3002/products"
    fetch(url)
      .then(resp => resp.json())
      .then(data => {
        let products = data.map((product, index) => {
          return (
            <div name='work' className='w-full md:h-screen text-gray-300 bg-[#0a192f]'>
              <div className='max-w-[1000px] mx-auto p-4 flex flex-col justify-center w-full h-full'>
                <div className='pb-8 w-full flex justify-center items-center flex-col'>
                  <p className='text-4xl font-bold inline border-b-4 text-gray-300 border-cyan-500'>Products</p>
                  <p className='py-6 text-2xl'>Check out some of our products</p>
                </div>
                {/* Container */}
                <div className='grid sm:grid-cols-2 md:grid-cols-3 gap-4'>
                  <div className='shadow-lg shadow-[#040c16] group container rounded-md flex justify-center items-center mx-auto content-div'>
                    <div className='opacity-0 group-hover:opacity-100 flex justify-center items-center flex-col'>
                      <span className=' text-lg font-bold text-white tracking-wider'>
                        Product Name: {product.productName}
                      </span>
                      <p className='text-center'>Product Inventory Quantity: {product.inventoryQuantity}</p>
                      <p className='text-center'>Product Ships on a Weekend: {product.shipOnWeekends ? "Yes" : "No"}</p>
                      <p className='text-center'>Max Shipping Date If Ordered Today:{ findDaysToShip(product).toDateString() }</p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          )
        })
        this.setState({products: products});
      })
  }
  render() {
    return (
      <div className="App">
        {this.state.products}
      </div>
    );
  }
}

export default App;
